import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const BookList = ({ searchQuery }) => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const fetchBooks = async () => {
      const response = await axios.get('https://www.googleapis.com/books/v1/volumes?q=' + searchQuery);
      setBooks(response.data.items);
    };
    fetchBooks();
  }, [searchQuery]);

  return (
    <div className="row">
      {books.map(book => (
        <div className="col-md-3" key={book.id}>
          <div className="card mb-4 shadow-sm">
            <img className="card-img-top" src={book.volumeInfo.imageLinks?.thumbnail} alt={book.volumeInfo.title} />
            <div className="card-body">
              <h5 className="card-title">{book.volumeInfo.title}</h5>
              <p className="card-text">{book.volumeInfo.authors?.join(', ')}</p>
              <Link to={`/book/${book.id}`} className="btn btn-primary">View Details</Link>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default BookList;
