// components/BookList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BookItem from './BookItem'; // Assuming BookItem component is defined separately

function BookList() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Function to fetch books from Google Books API
    const fetchBooks = async () => {
      try {
        const response = await axios.get('https://www.googleapis.com/books/v1/volumes', {
          params: {
            q: 'javascript' // Example query for books on JavaScript
          }
        });
        if (response.data.items) {
          const fetchedBooks = response.data.items.map(item => {
            // Check if volumeInfo is defined before destructuring
            const volumeInfo = item.volumeInfo || {};
            return {
              id: item.id,
              title: volumeInfo.title || 'Unknown Title',
              author: volumeInfo.authors ? volumeInfo.authors.join(', ') : 'Unknown Author',
              description: volumeInfo.description || 'No description available',
              genre: volumeInfo.categories ? volumeInfo.categories.join(', ') : 'Unknown Genre',
              coverImage: volumeInfo.imageLinks ? volumeInfo.imageLinks.thumbnail : '',
              price: item.saleInfo && item.saleInfo.retailPrice ? item.saleInfo.retailPrice.amount : 'Not for sale'
            };
          });
          setBooks(fetchedBooks);
        }
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks();
  }, []);

  return (
    <div className="book-list">
      {books.map(book => (
        <BookItem key={book.id} book={book} />
      ))}
    </div>
  );
}

export default BookList;
