import React, { useState } from 'react';
import data from '../volumes.json';
import './SearchBar.css'; 
import ImageDetails from './ImageDetails'

const SearchBar = () => {
    const [query, setQuery] = useState('');
    const [books, setBooks] = useState([]);
    const [error, setError] = useState(null);

    const fetchBooks = (query) => {
        try {
            // Filter the local data based on the query
            const filteredBooks = data.items.filter(book =>
                book.volumeInfo.title.toLowerCase().includes(query.toLowerCase()) ||
                book.volumeInfo.authors?.some(author => author.toLowerCase().includes(query.toLowerCase()))
            );
            setBooks(filteredBooks);
            setError(null);
        } catch (error) {
            console.error('Error fetching data:', error);
            setError('Error fetching data. Please try again.');
        }
    };

    const handleSearch = (e) => {
        e.preventDefault();
        fetchBooks(query);
    };

    return (
        <>
        <div className="search-bar">
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder="Search for books"
                />
                <button type="submit">Search</button>
            </form>
            {error && <p>{error}</p>}
            <ul>
                {books.map((book) => (
                    <li key={book.id}>
                        {book.volumeInfo.title} by {book.volumeInfo.authors?.join(', ')}
                    </li>
                ))}
            </ul>
        </div>
        
        <ImageDetails />
      
        </>
    );
};

export default SearchBar;
