import React, { useState } from 'react';

const Checkout = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [payment, setPayment] = useState('');

  const handleCheckout = (e) => {
    e.preventDefault();
    // Handle checkout logic here
    console.log('Checkout data:', { name, email, address, payment });
    alert('Order placed successfully!');
  };

  return (
    <div className="container mt-5">
      <h1>Checkout</h1>
      <form onSubmit={handleCheckout}>
        <div className="form-group">
          <label>Name</label>
          <input type="text" className="form-control" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input type="email" className="form-control" value={email} onChange={(e)=> setEmail(e.target.value)} required />
</div>
<div className="form-group">
<label>Address</label>
<textarea className="form-control" value={address} onChange={(e) => setAddress(e.target.value)} required />
</div>
<div className="form-group">
<label>Payment Information</label>
<input type="text" className="form-control" value={payment} onChange={(e) => setPayment(e.target.value)} required />
</div>
<button type="submit" className="btn btn-primary">Place Order</button>
</form>
</div>
);
};

export default Checkout;
