// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import BookList from './components/BookList';
import BookDetails from './components/BookDetails';
import ShoppingCart from './components/ShoppingCart';
import SearchBar from './components/SearchBar';
import Checkout from './components/Checkout';

const App = () => {
  const [books, setBooks] = useState([]);
  const [cart, setCart] = useState([]);

  const addToCart = (book) => {
    setCart([...cart, { ...book, quantity: 1 }]);
  };

  const removeFromCart = (book) => {
    setCart(cart.filter(item => item.id !== book.id));
  };

  const updateQuantity = (book, quantity) => {
    setCart(cart.map(item => item.id === book.id ? { ...item, quantity } : item));
  };

  return (
    <Router>
      <SearchBar setBooks={setBooks} />
      <Switch>
        <Route exact path="/" render={() => <BookList books={books} />} />
        <Route path="/books/:id" render={(props) => <BookDetails {...props} addToCart={addToCart} />} />
        <Route path="/cart" render={() => <ShoppingCart cart={cart} removeFromCart={removeFromCart} updateQuantity={updateQuantity} />} />
        <Route path="/checkout" component={Checkout} />
      </Switch>
    </Router>
  );
};

export default App;
