import React, { useState } from 'react';
import BookList from './BookList';
import BookDetails from './components/BookDetails'
import ShoppingCart from './ShoppingCart';
import SearchBar from './components/SearchBar';
import Checkout from './components/Checkout';

const App = () => {
  const [books, setBooks] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);

  const handleAddToCart = (book) => {
    const newItem = {
      id: book.id,
      title: book.volumeInfo.title,
      price: 10, // Assuming a fixed price for demonstration
      quantity: 1
    };
    setCartItems([...cartItems, newItem]);
  };

  const handleRemoveFromCart = (id) => {
    const updatedCart = cartItems.filter(item => item.id !== id);
    setCartItems(updatedCart);
  };

  const handleAdjustQuantity = (id, amount) => {
    const updatedCart = cartItems.map(item => {
      if (item.id === id) {
        return { ...item, quantity: item.quantity + amount };
      }
      return item;
    });
    setCartItems(updatedCart);
  };

  const handleSearch = (searchTerm) => {
    // Implement search functionality based on searchTerm
    // Example: filter books based on title, author, or genre
    // Update 'books' state accordingly
  };

  const handleSelectBook = (book) => {
    setSelectedBook(book);
  };

  const handleCheckout = () => {
    // Implement checkout logic here
    // Typically involves sending data to a server or API
    console.log('Implement checkout functionality');
  };

  return (
    <div className="app">
      <h1>Online Bookstore</h1>
      <SearchBar handleSearch={handleSearch} />
      <div className="main-content">
        <BookList books={books} handleSelectBook={handleSelectBook} handleAddToCart={handleAddToCart} />
        {selectedBook && <BookDetails book={selectedBook} handleAddToCart={handleAddToCart} />}
        <ShoppingCart cartItems={cartItems} removeFromCart={handleRemoveFromCart} adjustQuantity={handleAdjustQuantity} />
        <CheckoutForm handleCheckout={handleCheckout} />
      </div>
    </div>
  );
};

export default App;
