import React, { useState } from 'react';
import BookItem from './BookItem';

const BookList = () => {
  const [bookDetails, setBookDetails] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFetchBookDetails = async (id) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`https://www.googleapis.com/books/v1/volumes/${id}`);
      if (!response.ok) {
        throw new Error('Failed to fetch book details');
      }
      const data = await response.json();
      setBookDetails(data.volumeInfo);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  return (
    <div className="book-list">
      {/* Assuming books is an array of book objects with id and other details */}
     

      {loading && <div>Loading...</div>}
      {error && <div>Error: {error}</div>}

      {bookDetails && !loading && !error && (
        <div className="book-detail">
          <h1>{bookDetails.title}</h1>
          <h2>by {bookDetails.authors?.join(', ')}</h2>
          <img src={bookDetails.imageLinks?.thumbnail} alt={bookDetails.title} />
          <p>{bookDetails.description}</p>
          <p><strong>Genre:</strong> {bookDetails.categories?.join(', ')}</p>
          <p><strong>Published Date:</strong> {bookDetails.publishedDate}</p>
          <p><strong>Publisher:</strong> {bookDetails.publisher}</p>
        </div>
      )}
    </div>
  );
};

export default BookList;
