import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
// import  useCart  from '../services/cartService';

const BookDetail = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchBook = async () => {
      const response = await axios.get(`https://www.googleapis.com/books/v1/volumes/${id}`);
      setBook(response.data);
    };
    fetchBook();
  }, [id]);

  if (!book) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-4">
          <img src={book.volumeInfo.imageLinks?.thumbnail} alt={book.volumeInfo.title} className="img-fluid" />
        </div>
        <div className="col-md-8">
          <h1>{book.volumeInfo.title}</h1>
          <h4>by {book.volumeInfo.authors?.join(', ')}</h4>
          <p>{book.volumeInfo.description}</p>
          <p><strong>Genre:</strong> {book.volumeInfo.categories?.join(', ')}</p>
          <button className="btn btn-primary" onClick={() => addToCart(book)}>Add to Cart</button>
        </div>
      </div>
    </div>
  );
};

export default BookDetail;
