import React from 'react';
import { useCart } from '../services/cartService';

const Cart = () => {
  const { cartItems, removeFromCart, updateQuantity } = useCart();

  return (
    <div className="container mt-5">
      <h1>Shopping Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div className="row">
          {cartItems.map(item => (
            <div className="col-md-4 mb-4" key={item.id}>
              <div className="card">
                <img className="card-img-top" src={item.volumeInfo.imageLinks?.thumbnail} alt={item.volumeInfo.title} />
                <div className="card-body">
                  <h5 className="card-title">{item.volumeInfo.title}</h5>
                  <p className="card-text">{item.volumeInfo.authors?.join(', ')}</p>
                  <div className="d-flex justify-content-between">
                    <input type="number" value={item.quantity} onChange={(e) => updateQuantity(item.id, e.target.value)} className="form-control w-25" />
                    <button className="btn btn-danger" onClick={() => removeFromCart(item.id)}>Remove</button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Cart;
