import React, { useState } from 'react';
import { Link, BrowserRouter as Router, Route,Redirect, Routes } from 'react-router-dom';
import BookList from './components/BookList';
import BookDetail from './components/BookDetails';
import ShoppingCart from './components/Cart';
import SearchBar from './components/SearchBar';
import Checkout from './components/Checkout';
import Navbar from './components/Navbar';
import Login from './components/Login';

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  return (
    <Router>
      <div>
        <ul>
          {loggedIn ? (
            <div>
              <li><Link to="/home">Home</Link></li>
              <li><Link to="/cart">Cart</Link></li>
              <li><Link to="/search">Search</Link></li>
              <button onClick={handleLogout}>Logout</button>
            </div>
          ) : (
            <li><Link to="/login">Login</Link></li>
          )}
        </ul>
        <Routes>
          <Route path="/login">
            {loggedIn ? <Redirect to="/home" /> : <Login handleLogin={handleLogin} />}
          </Route>
          <Route path="/home">
            {loggedIn ? <Home /> : <Redirect to="/login" />}
          </Route>
          <Route path="/cart">
            {loggedIn ? <Cart /> : <Redirect to="/login" />}
          </Route>
          <Route path="/search">
            {loggedIn ? <Search /> : <Redirect to="/login" />}
          </Route>
          <Route path="/" exact>
            <Redirect to="/login" />
          </Route>
        </Routes>
      </div>
    </Router>
  );
};

const Logins = ({ handleLogin }) => (
  <div>
    <h2>Login Page</h2>
    <button onClick={handleLogin}>Sign In</button>
  </div>
);

const Home = () => <h2>Home Page</h2>;
const Cart = () => <h2>Cart Page</h2>;
const Search = () => <h2>Search Page</h2>;

export default App;
