import axios from 'axios';

const API_URL = 'https://www.googleapis.com/books/v1/volumes';

export const fetchBooks = (query) => {
  return axios.get(`${API_URL}?q=${query}`);
};

export const fetchBookDetails = (id) => {
  return axios.get(`${API_URL}/${id}`);
};
