import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import fetchBooks from '../server'

const BookDetail = () => {
  let { id } = useParams(); // Get book id from URL params
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchBooks = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/books');
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.text(); // Use response.text() to handle HTML response
      // Handle the HTML data as needed
      console.log(data); // For example, log the HTML data
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };
  

  useEffect(() => {
    // Fetch detailed book data based on id
    const fetchBookDetails = async () => {
      try {
        const response = await fetch(`https://books.google.com/books?uid=103234538657727512031&as_coll=1001${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch book details');
        }
        const data = await response.json();
        setBook(data.volumeInfo);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchBookDetails();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="book-detail">
      {book && (
        <>
          <h1>{book.title}</h1>
          <h2>by {book.authors?.join(', ')}</h2>
          <img src={book.imageLinks?.thumbnail} alt={book.title} />
          <p>{book.description}</p>
          <p><strong>Genre:</strong> {book.categories?.join(', ')}</p>
          <p><strong>Published Date:</strong> {book.publishedDate}</p>
          <p><strong>Publisher:</strong> {book.publisher}</p>
          {/* Add more book details as needed */}
        </>
      )}
    </div>
  );
};

export default BookDetail;
