// Cart.js
import React from 'react';
import { useCart } from './CartContext';

const Cart = () => {
  const { cart } = useCart();

  return (
    <div className="cart">
      <h2>Shopping Cart</h2>
      <ul>
        {cart.map((book, index) => (
          <li key={index}>
            {book.volumeInfo.title} by {book.volumeInfo.authors?.join(', ')}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Cart;
