import React from 'react';
import './ImageDetails.css';

const ImageDetails = () => {
  const images = [
    { src: '1.jpg', title: 'Getting things done', description: 'Mijn favoriet over het organiseren van je leven: Getting Things Done helpt je om nieuwe mentale vaardigheden te ontwikkelen die je nodig hebt in een tijd van multitasken en informatiestress.’ – The Wall Street Journal<br> <br> ‘Getting Things Done beschrijft een ongelooflijk praktische methode die drukke mensen weer controle geeft over hun leven. Het kan je helpen om succesvoller te worden. En belangrijker, het kan je helpen om een gelukkiger leven te leiden.’ – Marshall Goldsmith, auteur van Tot hier en nu verder!' },
    { src: '/images/image2.jpg', title: 'Image 2', description: 'Description for Image 2' },
    { src: '/images/image3.jpg', title: 'Image 3', description: 'Description for Image 3' },
  ];

  return (
    <div className="image-gallery">
      {images.map((image, index) => (
        <div key={index} className="image-card">
          <img src={image.src} alt={image.title} className="image" />
          <h3>{image.title}</h3>
          <p>{image.description}</p>
        </div>
      ))}
    </div>
  );
};

export default ImageDetails;
