import axios from 'axios';

const API_KEY = 'AIzaSyCrFbUJ8_MAJlhakEUy9KqLIm7kluPvzbk';
const BASE_URL = 'https://console.cloud.google.com/apis/credentials?project=emerald-stage-428411-r9';

export const fetchBooks = async (query) => {
  const response = await axios.get(`${BASE_URL}?q=${query}&key=${API_KEY}`);
  return response.data.items;
};

export const fetchBookById = async (id) => {
  const response = await axios.get(`${BASE_URL}/${id}?key=${API_KEY}`);
  return response.data;
};
