import React from 'react';

const BookItem = ({ book, onFetchBookDetails }) => {
  return (
    <div className="book-items">
      <img src={book.coverImage?.thumbnail} alt={book.title} />
      <h3>{book.title}</h3>
      <p>By {book.authors?.join(', ')}</p>
      <button onClick={() => onFetchBookDetails(book.id)}>Details</button>
      <button>Add to Cart</button>
    </div>
  );
};

export default BookItem;
