// components/BookList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BookItem from './BookItem'; // Assuming BookItem component is defined separately

function BookList() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Function to fetch books from Google Books API
    const fetchBooks = async () => {
      try {
        const response = await axios.get('https://www.googleapis.com/books/v1/volumes', {
          params: {
            q: 'javascript'
          }
        });
        if (response.data.items) {
          setBooks(response.data.items.map(item => ({
            id: item.id,
            title: item.volumeInfo.title,
            author: item.volumeInfo.authors ? item.volumeInfo.authors.join(', ') : 'Unknown Author',
            description: item.volumeInfo.description,
            genre: item.volumeInfo.categories ? item.volumeInfo.categories.join(', ') : 'Unknown Genre',
            coverImage: item.volumeInfo.imageLinks ? item.volumeInfo.imageLinks.thumbnail : '',
            price: item.saleInfo.retailPrice ? item.saleInfo.retailPrice.amount : 'Not for sale'
          })));
        }
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks();
  }, []);

  return (
    <div className="book-list">
      {books.map(book => (
        <BookItem key={book.id} book={book} />
      ))}
    </div>
  );
}

export default BookList;
