const express = require('express');
const fetch = require('node-fetch');
const app = express();
const port = 5000;

app.use((req, res, next) => {
  res.header('Access-Control-Allow-Origin', '*');
  res.header('Access-Control-Allow-Headers', 'Origin, X-Requested-With, Content-Type, Accept');
  next();
});

app.get('/api/books', async (req, res) => {
  try {
    const response = await fetch('https://books.google.com/books?uid=103234538657727512031&as_coll=1001');
    const data = await response.text(); // Use response.text() if the API returns HTML
    res.send(data); // Send the raw HTML response back to the client
  } catch (error) {
    res.status(500).send('Error fetching books');
  }
});

app.listen(port, () => {
  console.log(`Server is running on http://localhost:${port}`);
});
