import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Navigate , Routes } from 'react-router-dom';
import BookList from './components/BookList';
import BookDetail from './components/BookDetails';
import ShoppingCart from './components/Cart';
import SearchBar from './components/SearchBar';
import Checkout from './components/Checkout';
import Navbar from './components/Navbar';
import Login from './components/Login';


const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  return (
    <Router>
      <BookDetail/>
      <BookList/>
      <Checkout/>
      <div>
        <Navbar loggedIn={loggedIn} handleLogout={handleLogout} />
        <Routes>
          <Route 
            path="/login" 
            element={loggedIn ? <Navigate to="/home" /> : <Logins handleLogin={handleLogin} />} 
          />
          <Route 
            path="/home" 
            element={loggedIn ? <Home /> : <Navigate to="/login" />} 
          />
          <Route 
            path="/cart" 
            element={loggedIn ? <ShoppingCart /> : <Navigate to="/login" />} 
          />
          <Route 
            path="/search" 
            element={loggedIn ? <SearchBar /> : <Navigate to="/login" />} 
          />
          <Route 
            path="/" 
            element={<Navigate to="/login" />} 
          />
        </Routes>
      </div>
    </Router>
  );
};

const Logins = ({ handleLogin }) => (
  <div>
    <h2>Login Page</h2>
    <button onClick={handleLogin}>Sign In</button>
  </div>
);

const Home = () => <h2>Home Page</h2>;
const Cart = () => <h2>Cart Page</h2>;
const Search = () => <h2>Search Page</h2>;

export default App;
